mapper.select:
SELECT COUNT(*) FROM review_moderation_logs
WHERE 1=1

    AND review_id = #{reviewId}


    AND admin_id = #{adminId}


    AND action = #{action}


    AND created_at >= #{startDate}


    AND created_at <= #{endDate}

SELECT * FROM review_moderation_logs
WHERE 1=1

    AND review_id = #{reviewId}


    AND admin_id = #{adminId}


    AND action = #{action}


    AND created_at >= #{startDate}


    AND created_at <= #{endDate}

ORDER BY created_at DESC
