mapper.select:
SELECT id, room_id, old_status, new_status, reason, changed_by, order_id, created_at, deleted FROM room_status_logs WHERE room_id = #{roomId} AND deleted = 0
AND created_at >= #{startDate}
AND created_at <= #{endDate}
ORDER BY created_at DESC

